const n="<span style=\"font-size: 32px; font-weight: 600;\">CSS \u6587\u672C\u8207\u5B57\u9AD4</span>\n\n## \u5E38\u7528\u6587\u672C\u6A23\u5F0F\u5C6C\u6027\n```css\n.test {\n  /* \u80CC\u666F\u984F\u8272 */\n  background-color: #f36;\n  /* \u5B57\u9AD4\u984F\u8272 */\n  color: #ffffff;\n  /* \u6700\u5927\u5230255 \u900F\u660E\u5EA6\u70BA0~1*/\n  color: rgba(255, 0, 0, .6);\n  /* \u5B57\u9AD4\u7C97\u7D30(\u9810\u8A2D: normal(400), \u52A0\u7C97: bold(700)) */\n  font-weight: bold;\n  /* \u5B57\u9AD4\u98A8\u683C(\u9810\u8A2D normal) */\n  font-style: italic;\n  /* \u6587\u5B57\u5927\u5C0F */\n  font-size: 26px;\n  /* \n    \u6587\u672C\u5167\u5BB9\u4E4B\u524D\u7684\u7E2E\u9032\u91CF\n    2em: \u8868\u793A\u5169\u500B\u5B57\u7B26\u7684\u5BEC\u5EA6\n    %: \u4EE5\u8A72\u5167\u5BB9\u584A\u5BEC\u5EA6\u6C7A\u5B9A\n  */\n  text-indent: 2em;\n  /* \u6587\u5B57\u7279\u6548(\u9810\u8A2D: none, underline: \u5E95\u7DDA, line-through: \u522A\u9664\u7DDA) */\n  text-decoration: underline;\n  /* \n    \u884C\u9AD8:\n    1.75 \u4EE3\u8868\u8A72\u5B57\u9AD4\u5927\u5C0F x 1.75\u500D\n    \u4E5F\u53EF\u4EE5\u5BEB\u6210 175% \u6216 26x1.75=45.5px \u6216 1.75em\n    \u901A\u5E38\u4EE5\u500D\u6578\u8868\u793A \n  */\n  line-height: 1.75;\n}\n```\n\n## \u5B57\u9AD4\u5C6C\u6027\n```css\n/* \u64C1\u6709\u5B57\u9AD4\u6587\u4EF6\u6642\u53EF\u7528\u4EE5\u4E0B\u65B9\u5F0F\u5B9A\u7FA9\u5B57\u9AD4 */\n@font-face {\n  /* \u53D6\u540D */\n  font-family: 'webfont';\n  /* \u70BA\u5B57\u9AD4\u63D0\u4F9B\u4E00\u500B\u975E\u5E38\u5C0F\u7684\u963B\u585E\u9031\u671F\u548C\u7121\u9650\u7684\u4EA4\u63DB\u9031\u671F */\n  font-display: swap;\n  /* IE9*/\n  src: url('fonts/webfont.eot');\n  /* IE6-IE8 */\n  src: url('fonts/webfont.eot') format('embedded-opentype'),\n      /* chrome\u3001firefox */\n      url('fonts/webfont.woff2') format('woff2'),\n      url('fonts/webfont.woff') format('woff'),\n      /* chrome\u3001firefox\u3001opera\u3001Safari, Android, iOS 4.2+*/\n      url('fonts/webfont.ttf') format('truetype'),\n      /* iOS 4.1- */\n      url('fonts/webfont.svg') format('svg');\n}\n\n/* \n  \u6703\u5148\u67E5\u770B\u4F7F\u7528\u8005\u4E3B\u6A5F\u6709\u7121\u5B89\u88DD\u5B57\u9AD4\n  \u82E5\u7B2C\u4E00\u500B\u5B57\u9AD4\u6C92\u6709\u5247\u9678\u7E8C\u5F80\u4E0B\u5C0B\u627E\u5408\u9069\u7684\u5B57\u9AD4\n*/\n.para5 {\n  font-family: 'Times New Roman', serif, 'Microsoft Yahei';\n}\n\n/* \n  \u901A\u5E38\u4E2D\u6587\u5B57\u9AD4\u6703\u64FA\u5728\u82F1\u6587\u5B57\u9AD4\u5F8C\u9762\u4EE5\u5C08\u6CE8\u986F\u793A\u4E2D\u6587\n  \u5B57\u9AD4\u5982\u679C\u662F\u4E2D\u6587\u6216\u82F1\u6587\u4E2D\u9593\u6709\u7A7A\u683C\uFF0C\u7528\u5F15\u865F\u5305\u8D77\u6765\n  \u591A\u500B\u5B57\u9AD4\u8A2D\u7F6E\uFF0C\u5B57\u9AD4\u8207\u5B57\u9AD4\u4E4B\u95F4\u7528\u9017\u865F\u9694\u958B\n  \u5F15\u865F\u5D4C\u5957:\u5916\u9762\u4F7F\u7528\u96D9\u5F15\u865F\uFF0C\u88E1\u9762\u4F7F\u7528\u55AE\u5F15\u865F\n*/\n.para6 {\n  font-family: 'Times New Roman', serif, '\u5FAE\u8F6F\u96C5\u9ED1';\n}\n\nh3 {\n  font-family: 'webfont';\n  color: rgb(250, 50, 190);\n}\n```\n\n## \u5B57\u9AD4\u5408\u5BEB\u5C6C\u6027\n- \u5FC5\u9808\u5305\u542B: ```font-size``` ```font-family```\n- \u53EF\u9078\u64C7\u5305\u542B: ```font-style``` ```font-variant``` ```font-weight``` ```line-height```\n- ```font-style``` ```font-variant``` ```font-weight``` \u5FC5\u987B\u5728 ```font-size``` \u4E4B\u524D\n- ```line-height``` \u9700\u5728 ```font-size``` \u5F8C\u9762\uFF0C\u7531 ```/``` \u5206\u9694\uFF0C\u5982: ```16px/3```\n- font-family \u5FC5\u987B\u6700\u540E\u6307\u5B9A\n```css\n p {\n  font: bold italic 40px/1.5 \"Times New Roman\", \"\u5FAE\u8F6F\u96C5\u9ED1\";\n}\n```\n";export{n as default};
